import React, { useState } from 'react'
import Main from './Main'
import { Link, BrowserRouter, NavLink } from 'react-router-dom';

import logo from '../image/portfoliogallery.jpg';
import logo1 from '../image/portfoliogallery1.jpg';
import logo2 from '../image/portfoliogallery2.jpg';

const Portfolio= ()=>
 {
  const [show ,setShow]= useState(false);
  const closeModalHandler =()=> setShow(false);
 
  
    return( <div className="portfolio-page">
        <Main/>
       
        <center>
        <h1 style={{"font-size":"32px","color":"#eee8aa"}}>Portfolio</h1>
        <hr/>
        </center>
         <br/>
        
       <div className="container-fluid">
            <div class=" row">
               <div className="col portfolio-gallery">     
                 <img src={logo} alt="Avatar" className="image" />
                   <div className="middle">
                      <div className="btn btn-warning" onClick={()=>setShow(true)}>VIEW</div>
                      <Modal show={show} close={closeModalHandler}/>
                    </div>
                </div>
                <div className="col portfolio-gallery1">     
                     <img src={logo1} alt="Avatar" className="image1" />
                    <div className="middle1">
                    <div className="btn btn-primary" onClick={()=>setShow(true)}>View</div>
                    <Modal show={show} close={closeModalHandler}/>
                    </div>
                 </div>
                 <div className="col portfolio-gallery2">     
                 <img src={logo2} alt="Avatar" className="image2" />
                   <div className="middle2">
                      <div className="btn btn-info" onClick={()=>setShow(true)}>VIEW</div>
                      <Modal show={show} close={closeModalHandler}/>

                    </div>
                </div>
           </div>

        </div>

       

</div>

    )
    
}
export default  Portfolio



